  const data = crop_gender_data;

  const margin = { top: 40, right: 0, bottom: 5, left: 100 };
  const width = 475 
  const height = 320

  const tooltip = d3
    .select("body")
    .append("div")
    .attr("class", "plotTooltip")
    .style("position", "absolute");

  const svg = d3
    .create("svg")
    .attr("viewBox", [
      0,
      0,
      width + margin.left + margin.right,
      height + margin.top + margin.bottom
    ])
    // .attr("width", "85%")
    .attr("height", "90%");

  const xScale = d3
    .scaleBand()
    .domain(data.map((d) =&gt; d.country))
    .range([margin.left, width - margin.right])
    .padding(0.05);

  const yScale = d3
    .scaleBand()
    .domain([
      "Cereals",
      "Legumes",
      "Roots & Tubers",
      "Fruits & Vegetables",
      "Non-edible crops"
    ])
    .range([margin.top, height - margin.bottom])
    .padding(0.05);

  // const colorScale = d3.scaleSequential(d3.interpolateBlues).domain([0, 100]);
  const colorScale = d3
    .scaleLinear()
    .domain([21, 61])
    .range(["#F7D732", "#216729"]);

  svg
    .selectAll("rect")
    .data(
      data.flatMap((d) =&gt;
        [
          "Cereals",
          "Legumes",
          "Roots & Tubers",
          "Fruits & Vegetables",
          "Non-edible crops"
        ].map((type) =&gt; ({
          country: d.country,
          type,
          value: d[type]
        }))
      )
    )
    .join("rect")
    .attr("x", (d) =&gt; xScale(d.country))
    .attr("y", (d) =&gt; yScale(d.type))
    .attr("width", xScale.bandwidth())
    .attr("height", yScale.bandwidth())
    .attr("fill", (d) =&gt; (d.value !== null ? colorScale(d.value) : "white"))
    .on("mouseover", function (event, d) {
      mutable hover_data = d;
    })
    .on("mousemove", (event, d) =&gt; {
      tooltip
        .style("top", event.pageY - 10 + "px")
        .style("left", event.pageX + 10 + "px")
        .style("opacity", 1)
        .html(
          `${_lang(nbText.General.adminNames[d.country])}&lt;br&gt;${_lang(nbText.GenderedAfriAG.FemaleParticipationCrops.plot.crops[d.type])}: ${
            d.value !== null ? d.value + "%" : _lang(nbText.General.noDataAvailable)
          }`
        );
    })
    .on("mouseout", function (event, d) {
      mutable hover_data = null;
      tooltip.style("opacity", 0);
    });

  svg
    .append("g")
    .attr("transform", `translate(0, ${height - margin.bottom})`)
    .attr("stroke-width", ".5px")
    .call(d3.axisBottom(xScale).tickFormat((d) =&gt; _lang(nbText.General.adminNames[d])))
    .selectAll("text")
    .style("font-size", "8px");

  svg
    .append("g")
    .attr("transform", `translate(${margin.left}, 0)`)
    .attr("stroke-width", ".5px")
    .call(d3.axisLeft(yScale).tickFormat((d) =&gt; _lang(nbText.GenderedAfriAG.FemaleParticipationCrops.plot.crops[d])))
    .selectAll("text")
    .style("font-size", "8px");

  svg
    .append("g")
    .attr("transform", `translate(100,-10) scale(0.75)`)
    .append(heatmap_legend);

  return svg.node();
}

