      0,
      width + margin.left + margin.right,
      height + margin.top + margin.bottom
    ])
    .attr("width", "90%")
    .attr("height", "90%");

  const g = svg
    .append("g")
    .attr("transform", `translate(${margin.left},${margin.top})`);

