  const variablesToKeep = [
    "empowerment_index",
    "decisions",
    "domestic_violence",
    "education",
    "employment",
    "family_planning",
    "reproductive_health"
  ];
  const filteredData = Object.fromEntries(
    Object.entries(data).filter(([key, value]) =&gt; variablesToKeep.includes(key))
  );

  const variables = Object.keys(filteredData);
  variables.sort(
    (a, b) =&gt; variablesToKeep.indexOf(a) - variablesToKeep.indexOf(b)
  );

