roi = {
  function calculateMeanVariables(raw_data) {
    return raw_data.reduce((acc, obj) =&gt; {
      Object.keys(obj).forEach((key) =&gt; {
        if (typeof obj[key] === "number" && !isNaN(obj[key])) {
          acc[key] = (acc[key] || 0) + obj[key] / raw_data.length;
        }
      });
      return acc;
    }, {});
  }
  let roiData;
  if (country !== "SSA") {
    if (admin1_sel === "Full Country" || admin1_sel === null) {
      const raw_data = non_topo.filter((d) =&gt; d.admin0_name === country);
      roiData = [calculateMeanVariables(raw_data)];
    } else {
      roiData = non_topo.filter((d) =&gt; d.admin1_name === admin1_sel);
    }
  } else {
    roiData = [calculateMeanVariables(non_topo)];
  }
  return roiData;
}

regional_avg = {
  const surrounding = non_topo.filter((d) =&gt; d.admin1_name !== "na");

  const variablesToKeep = [
    "decisions",
    "domestic_violence",
    "education",
    "employment",
    "family_planning",
    "empowerment_index",
    "reproductive_health"
  ];

  // Calculate the mean value for each variable
  const meanValues = variablesToKeep.reduce((acc, variable) =&gt; {
    const sum = surrounding.reduce((sum, area) =&gt; sum + area[variable], 0);
    acc[variable] = sum / surrounding.length;
    return acc;
  }, {});

  return meanValues;
}

non_topo = {
  if (country === "SSA" || admin1_sel === "Full Country") {
    return db.query(`SELECT * FROM gender`);
  } else {
    return db.query(`SELECT * FROM gender WHERE admin0_name LIKE '${country}'`);
  }
}
























