biVar_insight = () =&gt; {
  let filtered = country === "SSA"
    ? BiVar_merge.filter((d) =&gt; d.admin1_name === null).map((d) =&gt; ({
        ...d,
        admin_name: d.admin0_name
      }))
    : BiVar_merge.filter(
        (d) =&gt; d.admin0_name === country && d.admin1_name !== null
      ).map((d) =&gt; ({ ...d, admin_name: d.admin1_name }));

  const heat_HotSpot = filtered.reduce(
    (max, obj) =&gt; obj.hs_value &gt; max.hs_value
      ? {
          admin_name: obj.admin_name,
          hs_value: obj.hs_value,
          ac_value: obj[G_variable]
        }
      : max,
    { admin_name: null, hs_value: -Infinity }
  );

  const classifyHSValue = (value) =&gt; {
    const thresholds = hazard_bivariate_thresholds[heat_hazard];
    return value &lt; thresholds.low
      ? "low"
      : value &lt;= thresholds.high
      ? "moderate"
      : "high";
  };

  const classifyACValue = (value) =&gt; {
    const values = country === "SSA"
      ? BiVar_merge.filter((d) =&gt; d.admin1_name === null).map((d) =&gt; d[G_variable])
      : BiVar_merge.filter((d) =&gt; d.admin1_name !== null).map((d) =&gt; d[G_variable]);
    const val_class = d3.scaleQuantile(values, d3.range(3))(value);
    return val_class === 0 ? "low" : val_class === 1 ? "moderate" : "high";
  };

  const gender_HotSpot = filtered.reduce(
    (extreme, obj) =&gt; G_variable === "women_in_agric"
      ? obj[G_variable] &gt; extreme.value
        ? {
            admin_name: obj.admin_name,
            value: obj[G_variable],
            hs_value: obj.hs_value
          }
        : extreme
      : obj[G_variable] &lt; extreme.value
      ? {
          admin_name: obj.admin_name,
          value: obj[G_variable],
          hs_value: obj.hs_value
        }
      : extreme,
    G_variable === "women_in_agric"
      ? { admin_name: null, value: -Infinity }
      : { admin_name: null, value: Infinity }
  );
  let classified_ac = classifyACValue(heat_HotSpot.ac_value)
  let classified_hs = classifyHSValue(gender_HotSpot.hs_value)

  let regionIntro = country === "SSA" ? {en: "", fr: ""} : {en: "", fr: "la region de"}
  let regionIntro_cap = country === "SSA" ? {en: "", fr: ""} : {en: "", fr: "La region de"}
  let template_fillers = [
    { name: "country", value: _lang(nbText.General.article_adminNames[country]) },
    { name: "regionIntro", value: _lang(regionIntro) },
    { name: "regionIntro_cap", value: _lang(regionIntro_cap) },
    { name: "highest_gender", value: translateA0_only(gender_HotSpot.admin_name)},
    { name: "HS_level", value: _lang(nbText.GenderHotspots.dynamicLevels[classified_hs.toLowerCase()])},
    { name: "heatstress_var", value: _lang(nbText.GenderHotspots.plot.heatLabels[heat_hazard]).toLowerCase() },
    { name: "highest_hs", value: translateA0_only(heat_HotSpot.admin_name)},
    { name: "AC_level", value: _lang(nbText.GenderHotspots.dynamicLevels[classified_ac.toLowerCase()])}];
  let md_text;
  let template
  if (G_variable === "women_in_agric") {
    template = _lang(nbText.GenderHotspots.dynamicInsight_womenInAgri)
  } else {
    template_fillers.push(
      { name: "gender_var", value:  _lang(nbText.GenderHotspots.domains[G_variable]).toLowerCase()}
    )
    template = _lang(nbText.GenderHotspots.dynamicInsight_FEMI)
  }

  return md`${Lang.reduceReplaceTemplateItems(template, template_fillers)}`

  function translateA0_only(adminName) {
    const translatedName = _lang(nbText.General.article_adminNames[adminName]);
    return translatedName === undefined ? adminName : translatedName;
  }
}

